import { Component } from '@angular/core';
import {Observable} from "rxjs";
import {Ingredient} from "../../ingredient/ingredient.model";
import {HttpClient} from "@angular/common/http";

@Component({
  selector: 'app-list-recipe',
  templateUrl: './list-recipe.component.html',
  styleUrls: ['./list-recipe.component.scss']
})
export class ListRecipeComponent {
  data$: Observable<Ingredient[]> | undefined; // Utilisez la classe Ingredient
  constructor(
      private http: HttpClient,

  ) {
  }

  ngOnInit(): void {
    this.data$ = this.getData();
  }
  getData(): Observable<Ingredient[]> {
    return this.http.get<Ingredient[]>('http://localhost:3000/recipe');
  }
}
<hr>
<div *ngIf="data$ | async as data" >
    <mat-card *ngFor="let recipe of data">
        <mat-card-header>
            {{ recipe.name }}
        </mat-card-header>
        <mat-card-content>
            <p>ID: {{ recipe.id }}</p>
            <!-- <ul *ngFor="let recipeIng of recipe.ingredients">
                <li>{{recipeIng}}</li>
            </ul> -->
        </mat-card-content>
    </mat-card>
</div>

<h4 *ngIf='!data$' class="center">
    <mat-spinner></mat-spinner>
</h4>
